import React, {Component} from 'react';
import axios from 'axios';
import SubmitAnswer from './SubmitDistance';
import auth0Client from '../Auth';

class Distance extends Component {
  constructor(props) {
    super(props);
    this.state = {
      distance: null,
    };

    this.submitDistance = this.submitDistance.bind(this);
  }

  async componentDidMount() {
    await this.refreshfsDistance();
  }

  async refreshDistance() {
    const { match: { params } } = this.props;
    const distance = (await axios.get(`http://localhost:8081/${params.questionId}`)).data;
    this.setState({
      distance,
    });
  }

  async submitDistance(distance) {
    await axios.post(`http://localhost:8081/answer/${this.state.question.id}`, {
      distance,
    }, {
      headers: { 'Authorization': `Bearer ${auth0Client.getIdToken()}` }
    });
    await this.refreshDistance();
  }

  render() {
    const {distance} = this.state;
    if (distance === null) return <p>Loading ...</p>;
    return (
      <div className="container">
        <div className="row">
          <div className="jumbotron col-12">
            <h1 className="display-3">{distance.title}</h1>
            <p className="lead">{distance.description}</p>
            <hr className="my-4" />
            <SubmitAnswer questionId={distance.id} submitAnswer={this.submitDistance} />
            <p>Distance</p>
            {
              distance.answers.map((distance, idx) => (
                <p className="lead" key={idx}>{distance.answer}</p>
              ))
            }
          </div>
        </div>
      </div>
    )
  }
}

export default Distance;
