import React, {Component, Fragment} from 'react';
import {Link} from 'react-router-dom';
import axios from 'axios';
import {withRouter} from 'react-router-dom';
import auth0Client from '../Auth';
import SubmitDistance from './SubmitDistance'

const styles = {
  CardHeader: {
    fontSize: '50px',
    fontWeight: 'bold',
    fontFamily: 'Courier New',
  },

  CardBody: {
    fontSize: '30px',
    fontFamily: 'Courier New',
    fontWeight: 'bold',
    color: 'black',
  },
};

class Distance extends Component {
  constructor(props) {
    super(props);

    this.state = {
      distance: null,
    };
  }

  updateDistance(value) {
    this.setState({
      distance: value,
    });
  }

  submit() {
    this.props.SubmitDistance(this.state.distance);

    this.setState({
      distance: '',
    });
  }

  render() {
    return (
      <div className="container">
        <div className="row" style = {{paddingTop: '10px'}}>
            <div className="card text-white bg-info mb-3">
              <div className="card-header" style = {styles.CardHeader}>
                Furthest you're willing to travel?
              </div>
              <div className="card-body">
                <h4 className="card-title" style = {styles.CardBody}>
                    <Fragment>
                        <div className="form-group text-center">
                            <label htmlFor="exampleInputEmail1">Answer:</label>
                                <input
                                    type="text"
                                    onChange={(e) => {this.updateDistance(e.target.value)}}
                                    className="form-control"
                                    placeholder="Share your answer."
                                    value={this.state.distance}
                                />
                        </div>
                            <button
                            className="btn btn-primary"
                            onClick={() => {this.submit()}}>
                            Submit
                            </button>
                            <hr className="my-4" />
                    </Fragment>
                </h4>
              </div>
            </div>
        </div>
      </div>
    )
  }
}

export default Distance;
