import React, {Component, Fragment} from 'react';
import {withRouter} from 'react-router-dom';
import auth0Client from '../Auth';

class SubmitDistance extends Component {
  constructor(props) {
    super(props);
    this.state = {
      distance: '',
    };
  }

  updateAnswer(value) {
    this.setState({
      distance: value,
    });
  }

  submit() {
    this.props.submitAnswer(this.state.distance);

    this.setState({
      distance: '',
    });
  }

  render() {
    if (!auth0Client.isAuthenticated()) return null;
    return (
      <Fragment>
        <div className="form-group text-center">
          <label htmlFor="exampleInputEmail1">Answer:</label>
          <input
            type="text"
            onChange={(e) => {this.updateDistance(e.target.value)}}
            className="form-control"
            placeholder="Share your answer."
            value={this.state.distance}
          />
        </div>
        <button
          className="btn btn-primary"
          onClick={() => {this.submit()}}>
          Submit
        </button>
        <hr className="my-4" />
      </Fragment>
    )
  }
}

export default withRouter(SubmitDistance);
